const storesSortedByStartTimeAsc = stores
  .slice()
  .sort((a, b) => b.price - a.price);

const storesSortedByEndTimeDesc = stores
  .sort((a, b) => b.endTime - a.endTime);


// let currentMinPrice;

const result = [];

let startTime;
let endTime;
let price;

for (let i=storesSortedByStartTimeAsc[0].startTime; i <= storesSortedByEndTimeDesc[0].endTime; i ++) {
  // console.log(i, getMinPrice(i));
  const minPrice = getMinPrice(i);

  if (typeof startTime === 'undefined') {
    startTime = i;
  }

  if (typeof price === 'undefined') {
    price = minPrice;
  } else if (minPrice !== price) {
    endTime = i;
    result.push({
      startTime,
      endTime,
      price,
    });

    startTime = endTime;
    price = minPrice;
  }
}

console.log(result);



function getMinPrice(currentTime) {
  let minPrice;

  const matchedStores = stores.filter((o) => {
    if (currentTime >= o.startTime && currentTime < o.endTime) {
      return true;
    }
  });

  matchedStores.forEach((o) => {
    if (!minPrice || o.price < minPrice) {
      minPrice = o.price;
    }
  });

  return minPrice;
}

